local a={Item=Isaac.GetItemIdByName("Dead Bird"),BleedDuration=60*3}function a:DamageTaken(b,c,c,d)if not b:IsEnemy()then return end;if d.Type==EntityType.ENTITY_FAMILIAR and d.Variant==FamiliarVariant.DEAD_BIRD then if b:HasEntityFlags(EntityFlag.FLAG_SLOW)then return end;local e=d.Entity:ToFamiliar().Player;b:AddSlowing(EntityRef(e),60,0.5,Color(0.5,0.3,0.3,1,0,0,0))end end;return function(f)f:AddCallback(ModCallbacks.MC_ENTITY_TAKE_DMG,a.DamageTaken)if not EID then return end;EID:addCollectible(a.Item,"A bird familiar will appear whenever Isaac takes damage#The bird chases enemies and deals contact damage#The bird will slow enemies it touches")end