local a={Item=Isaac.GetItemIdByName("Lemon Mishap"),ConfusionDuration=60*5}function a:DamageTaken(b,c,c,d)if b:IsEnemy()and d.Variant==EffectVariant.PLAYER_CREEP_LEMON_MISHAP and not b:HasEntityFlags(EntityFlag.FLAG_CONFUSION)then b:AddConfusion(d,a.ConfusionDuration,false)end end;return function(e)e:AddCallback(ModCallbacks.MC_ENTITY_TAKE_DMG,a.DamageTaken)if not EID then return end;EID:addCollectible(a.Item,"Spawns a tiny pool of creep#Deals 24 damage per second#{{Confusion}} Enemies that touch the creep will become confused")end