local a={Enemy=Isaac.GetEntityTypeByName("Mom's Hand"),WasTouched=false,RoomTarget=nil}function a:OnUpdate(b)local c=b:GetSprite()if b.FrameCount==1 and b.Type==EntityType.ENTITY_MOMS_HAND then c:Play("JumpUp",true)end;if a.WasTouched and a.RoomTarget==nil then local d=Game():GetLevel():GetLastRoomDesc()local e=d.Data;if e.Type==RoomType.ROOM_CURSE or e.Type==RoomType.ROOM_ERROR then a.RoomTarget=Game():GetLevel():GetStartingRoomIndex()else a.RoomTarget=Game():GetLevel():GetPreviousRoomIndex()end end end;function a:Reset()a.RoomTarget=nil;a.WasTouched=false end;function a:OnNewRoom()if a.RoomTarget~=nil and a.WasTouched then a.WasTouched=false;Game():ChangeRoom(a.RoomTarget)a.RoomTarget=nil end end;function a:OnPlayerTouch(f,g)if g.Type==EntityType.ENTITY_MOMS_HAND and a.WasTouched==false then a.WasTouched=true end end;return function(h)h:AddCallback(ModCallbacks.MC_NPC_UPDATE,a.OnUpdate)h:AddCallback(ModCallbacks.MC_PRE_GAME_EXIT,a.Reset)h:AddCallback(ModCallbacks.MC_POST_GAME_END,a.Reset)h:AddCallback(ModCallbacks.MC_POST_NEW_ROOM,a.OnNewRoom)h:AddCallback(ModCallbacks.MC_PRE_PLAYER_COLLISION,a.OnPlayerTouch)end