local a={Trinket=Isaac.GetTrinketIdByName("Fish Head"),Chance=1/4,BlacklistedFlags={DamageFlag.DAMAGE_FIRE,DamageFlag.DAMAGE_CLONES,DamageFlag.DAMAGE_POOP,DamageFlag.DAMAGE_DEVIL,DamageFlag.DAMAGE_TNT,DamageFlag.DAMAGE_IV_BAG,DamageFlag.DAMAGE_FAKE,DamageFlag.DAMAGE_CHEST}}function a:OnEntityHurt(b,c,d,e)local f=b:ToPlayer()if not f:HasTrinket(a.Trinket)then return end;local g=f:GetTrinketRNG(a.Trinket)local h=g:RandomFloat()if h<a.Chance then for c=1,f:GetTrinketMultiplier(a.Trinket)do Isaac.Spawn(EntityType.ENTITY_FAMILIAR,FamiliarVariant.BLUE_FLY,LocustSubtypes.LOCUST_OF_PESTILENCE,f.Position,Vector(0,0),f)end end end;return function(i)i:AddCallback(ModCallbacks.MC_ENTITY_TAKE_DMG,a.OnEntityHurt,EntityType.ENTITY_PLAYER)if EID then EID:addTrinket(a.Trinket,"1/4 chance to spawn 1 Locust of Pestilence when Isaac takes damage#Locust deals double your damage and applies poison")end;return{OldItemId=TrinketType.TRINKET_FISH_HEAD,NewItemId=a.Trinket}end