local a=require("BalanceMod.Utility.PlayerTracker")local b={Trinket=Isaac.GetTrinketIdByName("Nazar"),EnemiesToRemove={},SwapType=0}function b:OnNPCSpawn(c)if b.EnemiesToRemove[GetPtrHash(c)]then local d=c.Position;c:Remove()if not c:Exists()then b.EnemiesToRemove[GetPtrHash(c)]=nil end;Game():SpawnParticles(d,EffectVariant.POOF01,1,0)return end;if c.FrameCount==0 then if c:IsChampion()then local e=Game():GetNumPlayers()for f=0,e-1 do local g=Isaac.GetPlayer(f)if g:HasTrinket(b.Trinket)then Isaac.Spawn(c.Type,c.Variant,c.SubType,c.Position,c.Velocity,c.Parent)b.EnemiesToRemove[GetPtrHash(c)]=true;break end end end end end;return function(h)h:AddCallback(ModCallbacks.MC_NPC_UPDATE,b.OnNPCSpawn)if not EID then return end;EID:addTrinket(b.Trinket,"Turns all champions into their non-champion variant#Converted enemies will not drop champion loot")end